#include <stdlib.h>
char* longestPalindrome(char* s)
{
    char *start, *end, *sp=s, *oldS, *head=s;
    int curLen=0, len=0, newPa=0;
    if(s==NULL || s[0]=='\0' || s[1]=='\0')
        return s;

    s++;
    while(*s)
    {
        if(*s==*sp && sp>=head)//遇到相同的字母，偶数的回文
        {
            if(sp+1==s)//新的回文
            {
                curLen = 2;
                newPa = 1;//新的回文

                s++;
                while(*s && (*s==*sp))//重复的字符
                {
                    curLen++;
                    s++;
                }
                oldS = sp+1;

                sp--;
            }
            else if(newPa)//正在处理回文
            {
                curLen += 2;
                sp--;
                s++;
            }
        }
        else if( *(s+1) && (sp+1==s) && (*(s+1)==*sp) )//奇数的回文
        {
            oldS = s;

            curLen = 3;

            newPa = 1;
            s += 2 ;
            sp--;
        }
        else if(newPa)//正在处理回文，遇到不同字母，结束
        {
            if(curLen>len)
            {
                start = sp+1;
                end = s-1;
                len = curLen;
            }
            s = oldS+1;

            newPa = 0;//回文结束
            sp = s-1;
        }
        else
        {
            sp++;
            s++;
        }
    }
    if(newPa)//回文到字符串末尾
    {
        if(curLen>len)
        {
            start = sp+1;
            end = s-1;
        }
    }

    *(end+1) = '\0';
    return start;
}
